import { defineStore } from 'pinia'
import api from "../axios"

export const useUsuarioStore = defineStore("usuario", {
    state: () => ({
        usuario: {},
    }),
    getters: {
      getUsuario(state){
          return state.usuario
        }
    },
    actions: {
        async login(data){
            try {
                await api.post('/login', data);
            } catch (error) {
                alert(error)
                console.log(error)
            }
        }
    },
  });
