import { defineStore } from 'pinia'
import api from "../axios"

export const useFornecedorStore = defineStore("fornecedor", {
    state: () => ({
        fornecedores: [],
    }),
    getters: {
      getFornecedores(state){
        return state.fornecedores
      },

      getFornecedoresToTable(state){
        return state.getFornecedores.map(function(obj) {
          return Object.keys(obj).map(function(chave) {
              return obj[chave];
          });
      }); 
      }
    },
    actions: {
      async fetchFornecedores() {
        try {
          console.log('teste');
          const data = await api.get('/fornecedores')
          this.fornecedores = data.data
        }
          catch (error) {
            alert(error)
            console.log(error)
        }
      },

      async addFornecedor(data){
        try {
          await api.post('/fornecedores', data);
        } catch (error) {
          alert(error)
          console.log(error)
        }
      }
    },
  });
